export default function NewMap({ latitude, longitude }) {
    const [facts, setFacts] = useState([]); // Initialize as an empty array
    const mapRef = useRef(null);
    const mapInstance = useRef(null); // Ref to store the Google Map instance
    const advancedMarkerInstance = useRef(null); // Ref to store the AdvancedMarkerElement instance
  
    const getQuestionsAndFact = async () => {
      try {
        const res = await fetch("/api/questions", {
          method: "POST",
          headers: {
            "Content-Type": "application/json", // Ensure the server understands JSON
          },
          body: JSON.stringify({
            lat: latitude, // Pass latitude
            lng: longitude, // Pass longitude
          }),
        });
  
        const mapData = await res.json();
        setFacts(mapData); // Update the facts state after fetching the data

      } catch (e) {
        console.log(e);
      }
    };
  
    useEffect(() => {
      const initializeMap = async () => {
        const loader = new Loader({
          apiKey: process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY,
          version: "quarterly",
        });
  
        const { Map } = await loader.importLibrary("maps");
        const { AdvancedMarkerElement } = await loader.importLibrary("marker");
  
        // Initialize the map only once and store it in the mapInstance ref
        mapInstance.current = new Map(mapRef.current, {
          center: { lat: latitude, lng: longitude },
          zoom: 17,
          gestureHandling: "none", // Disable all user gestures (scroll, drag, touch)
          zoomControl: false, // Disable zoom control
        });
  
        // Initialize the advanced marker and store it in the advancedMarkerInstance ref
        const markerElement = document.createElement('div');
        markerElement.innerHTML = `
          <img src=${quiz} alt="Marker Image" 
          style="width: 50px; height: 50px; cursor: pointer;" />
        `;
  
        advancedMarker = new AdvancedMarkerElement({
          position: { lat: latitude, lng: longitude },
          content: markerElement, // Use the custom marker with Data URI
          map: mapInstance.current, // Add marker to the map
          title: "My location",
          gmpClickable: true,
          mapId: NEXT_PUBLIC_GOOGLE_MAPS_ID
        });
  
        // Add a click listener to the marker
        advancedMarkerInstance.current.addListener("click", () => {
          console.log("Marker clicked");
        });
      };
  
      if (!mapInstance.current) {
        // Initialize the map and marker only once
        getQuestionsAndFact().then(() => initializeMap()).catch((error) => {
          console.log(error);
        });
      }
    }, []); // Only run once on mount
  
    // New useEffect to create markers once facts are loaded
    useEffect(() => {
      if (mapInstance.current && facts.length > 0) {
        const { AdvancedMarkerElement } = google.maps.importLibrary("marker");
  
        // Map over the facts and create markers
        facts.forEach((element, index) => {

        console.log(element)
          const markerElement = document.createElement('div');
          markerElement.innerHTML = `
            <img src=${quiz} alt="Marker Image" 
            style="width: 150px; height: 150px; cursor: pointer;" />`
  
          const additionalMarker = new AdvancedMarkerElement({
            position: { lat: element.lat, lng: element.lng },
            //content: markerElement,
            map: mapInstance.current, // Add marker to the map
            title: element.fact,
            gmpClickable: true,
            mapId: NEXT_PUBLIC_GOOGLE_MAPS_ID
          });
  
          additionalMarker.addListener("click", () => {
            console.log(`Marker clicked for fact: ${element.fact}`);
          });
        });
      }
    }, [facts]); // This effect will run every time facts is updated
  
    useEffect(() => {
      if (mapInstance.current && advancedMarkerInstance.current) {
        // Update map center and marker position when latitude or longitude changes
        const newCenter = { lat: latitude, lng: longitude };
        mapInstance.current.setCenter(newCenter); // Update the map's center
        advancedMarkerInstance.current.position = newCenter; // Update the marker's position
      }
    }, [latitude, longitude]); // This useEffect will run every time latitude or longitude changes
  
    const containerStyle = {
      width: "100vw", // Use full width of the parent container
      height: "100vh", // Use full height of the parent container
      position: "relative", // To allow overlaying inputs/buttons on top
    };
  
    return (
      <Box>
        <div ref={mapRef} style={containerStyle}></div>
      </Box>
    );
  }


